CommandUIHandler.CommandAction:
javascript:
            function resultCallback(result, value)
            {
              if (result == 1)
              {
                SP.UI.Notify.addNotification('Conversion Request Queued');
              }
              else if (result == 2)
              {
                SP.UI.Notify.addNotification('Conversion Request Failed');
              }
            }

            var ctx = SP.ClientContext.get_current();
            var items = SP.ListOperation.Selection.getSelectedItems(ctx);
            var cItems = '';
            var i;

            for (i in items)
            {
              cItems += '|' + items[i].id;
            }

            if(cItems != '')
            {
              var options = {
              url: '{SiteUrl}/_layouts/Nauplius.WAS/Conversion.aspx?Items=' + cItems + '&ListId={SelectedListId}&Source={Source}',
              allowMaximize: true,
              showClose: true,
              width: 700,
              height: 460,
              dialogReturnValueCallback: resultCallback};
              SP.UI.ModalDialog.showModalDialog(options);
            }

javascript:
            function resultCallback(result, value)
            {
            }

            var ctx = SP.ClientContext.get_current();
            var options = {
              url: '{SiteUrl}/_layouts/Nauplius.WAS/History.aspx',
              allowMaximize: true,
